import {Fragment,useRef,useState,useContext} from 'react';
import AuthContext from '../UI/Context';
import classes from './Login.module.css';
const Login=()=>{
    const Authcntx=useContext(AuthContext)
    const [isLogin,setisLogin]=useState(true);
    const emailInputRef=useRef();
    const passwordInputRef=useRef();
    const EventHandler=(event)=>{
        event.preventDefault();
        const enteredEmail=emailInputRef.current.value;
        const enteredPassword=passwordInputRef.current.value;
        let url;
    if(isLogin){
      url='https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyCewUWbgztJYjhsb5UEmf3Ni6T_ehpNmXQ';
    }
    else{
      url='https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyCewUWbgztJYjhsb5UEmf3Ni6T_ehpNmXQ'
    }
      fetch(url,{
        method:'POST',
        body:JSON.stringify(
          {
            email:enteredEmail,
            password:enteredPassword,
            returnSecureToken:true
          }
        ),
        headers :{
          'Content-Type':'application/json'
        }
      }
      ).then(res=>{
        setIsLoading(false);
        if(res.ok){
          return res.json();

        }
        else{
          return res.json().then((data)=>{
            let errorMessage='Authentication Failed';
            throw new Error(errormessage);
          })
        }
      }).then((data)=>{
        console.log(data);
        Authcntx.logIn(data.idToken)
        history.replace('/profile')
      })
      .catch((err)=>{
        alert(err.message)
      })   }
    return(
        <Fragment>
            <form className={classes.auth} onSubmit={EventHandler}>
                <div>
                    <label>Email</label>
                    <input type='email' required ref={emailInputRef}/>
                    <br/>
                    &nbsp;
                    </div>
                    <div>
                    <label>Password</label>
                    <input type='number' required ref={passwordInputRef}/><br/>
                    &nbsp;
                    </div>
                    <button type='submit'>Login</button>
            </form> 
        </Fragment>
    )
}
export default Login;
