import React, { Fragment,useContext,useEffect,useState} from 'react';
import { Button,Figure,Container,Row,Col,Card } from 'react-bootstrap';
import Model from '../UI/Model';
import classes from './CartItem.module.css'
import CartContext from '../store/CartContext';
import axios from 'axios';
const CartItem=(props)=>{
    const Cartcntx=useContext(CartContext);
    const[value,setValue]=useState([])
    let totalAmount=0;
    value.forEach(item=>{
          totalAmount=totalAmount+Number(item.amount);
    })
    

    const onRemoveHandler=id=>{
        axios.delete(`https://crudcrud.com/api/dfabbf65ff814062a27a71f3356f6db9/mail/${id}`)
        .then((res)=>{
            const posts = res.data.filter(items => items.id!== id);  
            console.log(id);
            setValue(posts);  
        })

    }
    const onAddHandler=item=>{
        Cartcntx.addItem({...item,quantity:1})

    }
        useEffect(()=>{
            axios.get("https://crudcrud.com/api/dfabbf65ff814062a27a71f3356f6db9/mail")
            .then((res)=>{
                setValue(res.data);
            })

        },[])
        
    return(
        <div class="justify-content-end">
            <Container>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
                <Row> 
                    <Col xs={7}>
                        <Card>
                            <Card.Body>
                                <h2 style={{textAlign:"center"}}>Cart</h2>
                            <div class="position-relative">
                                <div class="position-absolute top-0 start-100">
                                <Button variant="danger" onClick={props.onClose}>X</Button>{' '}
                                </div>
                            </div>    
                            <ul >{value.map(item=>
                        <li >
                                <div>
                                <Figure> 
                                <Figure.Image
                                width={70}
                            height={70}
                            alt="100x100"
                            src={item.imageUrl}
                        />
                        </Figure>  Item:{item.title}  price:{item.amount}   Quantity:{item.quantity}
                        <div> 
                                <button onClick={onRemoveHandler.bind(null,item.id)}>-</button>
                                <button onClick={onAddHandler.bind(null,item)}>+</button>
                            </div>
                        </div>
                            </li>)}</ul>
                                
                                <div className={classes.total}>
                                    <span>Total Amount:</span>
                                    <span >{totalAmount}</span>
                                </div>
                            <div class="position-relative">
                                <div class="position-absolute top-0 start-50 translate-middle-x">
                                    <Button variant="info">Purchase</Button>
                                </div>    
                            </div>
                       
                            </Card.Body>
      
                        </Card>
                    </Col>
                </Row>
            </Container>
           </div> 
    )
}
export default CartItem;
