    axios.get("https://crudcrud.com/api/1137aa37bd6745aba2320363c9ed4fb8/cart")
        .then((res)=>{

        })

  }
  const HideHandler=()=>{
    setCreate(false);
  }
  return (
    <CreateProvider>
      <Container>
      <Cart onShowCart={CartHandler}/>
      </Container>
      {create&&<CartItem onClose={HideHandler}/>}  
      {Authcntx.isLoggedIn&&<Products />}
      {!Authcntx.isLoggedIn&&<Navigate to='/login'/>}

    </CreateProvider>
  );
}  
export default App;
