import React,{Fragment,useContext} from 'react';
import { Card, Container,Row,Col,Figure, Button } from 'react-bootstrap';
import CartContext from './store/CartContext';
import { Link } from 'react-router-dom';
import AddCart from './AddCart';
import Cart from './Cart'
import ProductDetails from './Router/ProductDetails';
let productsArr=[
    {

        title:'Album 1',
        
        amount: 100,
        
        imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%201.png',
        id:'s1',
        quantity:1
        
        },
        
        {
        
        title: 'Album 2',
        
        amount: 50,
        
        imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%202.png',
        id:'s2',
        quantity:1
        },
        
        {
        
        title: 'Album 3',
        
        amount: 70,
        
        imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%203.png',
        id:'s3',
        quantity:1
        },
        
        {
        
        title: 'Album 4',
        
        amount: 100,
        
        imageUrl: 'https://prasadyash2411.github.io/ecom-website/img/Album%204.png',
        id:'s4',
        quantity:1
        
        }
]
const Products=(props)=>{
    const Cartcntx=useContext(CartContext)
    
    let List=productsArr.map(item=><li key={item.id} >
        <Container>
                <Row>
                    <Col xs={7}>
                        <Card>
                            <Card.Body><h2>
                                <Link to={`/productsDetails/${item.id}`}>{item.title}</Link>
                            </h2>
    <h6 style={{textAlign:'revert'}}>${item.amount}</h6> <Figure>
      <Figure.Image
        width={171}
        height={180}
        alt="171x180"
        src={item.imageUrl}
      />
      </Figure>
      <AddCart item={item}/>
      </Card.Body>
      
                        </Card>
                    </Col>
                </Row>
            </Container>
    </li>)
        return(
       <Fragment>
            <h2 style={{textAlign:"center"}}>Music</h2>   
           <div>{List}</div>
        </Fragment>
    ) 
}
export default Products;
