import React,{useContext,useEffect, useState} from 'react';
import { Button } from 'react-bootstrap';
import { propTypes } from 'react-bootstrap/esm/Image';
import CartContext from './store/CartContext';
import axios from 'axios';
const AddCart=(props)=>{
    const Cartcntx=useContext(CartContext);
    const [putt,setPutt]=useState([]);
    
    const EventHandler=(event)=>{
        event.preventDefault();
        Cartcntx.addItem(props.item);
        const obj=props.item
        const existingCartIndex=putt.findIndex(
            (item)=>item.title===putt.item.title
        )
        const existingCart=putt.items[existingCartIndex];
        if(existingCart){
            axios.put("https://crudcrud.com/api/06558e9fa53643aaa723725555ca0b59/mail",{quantity:existingCart.quantity+action.item.quantity})
                .then((res)=>{
                   setPutt(res.data); 
                })
        }
        else{
            axios.post("https://crudcrud.com/api/06558e9fa53643aaa723725555ca0b59/mail",obj)
                .then((res)=>{
                   setPutt(res.data);
                })
          
        }
        
    }
    return (
        <div class="position-end">
        <Button variant="info" onClick={EventHandler}>Add to Cart</Button>{' '}
        </div>
    )
}
export default AddCart;
