import React,{useContext,useState} from 'react';
import { Button } from 'react-bootstrap';
import { propTypes } from 'react-bootstrap/esm/Image';
import CartContext from './store/CartContext';
import axios from 'axios';
const AddCart=(props)=>{
    const Cartcntx=useContext(CartContext)
    const [name,setName]=useState([]);
    const EventHandler=(event)=>{
        event.preventDefault();
        Cartcntx.addItem(props.item);
        const obj=props.item
        const value=name.filter(item=>{
            item._id===obj._id
        })
        if(obj.title=="Ablbum 1")
        {
            axios.put("https://crudcrud.com/api/06558e9fa53643aaa723725555ca0b59/mail",{quantity:2})
            .then((res)=>{
               setName(res.data);
            }) 

        }
        else{
            axios.post("https://crudcrud.com/api/06558e9fa53643aaa723725555ca0b59/mail",obj)
                .then((res)=>{
                   setName(res.data);
                }) 

        }
        
         
              
        
    }
    return (
        <div class="position-end">
        <Button variant="info" onClick={EventHandler}>Add to Cart</Button>{' '}
        </div>
    )
}
export default AddCart;
